sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function () {
		this.rightnow = new GlideDateTime();
	},
    isDatePast: function (strDate) {

        var testdate = new GlideDateTime(strDate);

        if (testdate.before(this.rightnow)) {
            return true;
        } else {
            return false;
        }
    },
    isDateToday: function (strDate) {

        var today = this.rightnow.getLocalDate();
        var istoday = strDate.getLocalDate();

        if (today.compareTo(istoday) == 0) {
            return true;
        }else{
            return false;
        }
    },
    type: 'NeedItUtils'
};
